INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u1-l1-1', 'sf-u1-l1', 'What is the meaning of "بِسْمِ"?', 'In the name of', ARRAY['Your name', 'I do not', 'did (deeds)'], 'بِسْمِ (bismi) means "In the name of".', 1),
  ('quiz-sf-u1-l1-2', 'sf-u1-l1', 'What is the meaning of "اللَّهِ"?', 'Allah (God)', ARRAY['advised each other', 'to your Lord', 'righteous deeds'], 'اللَّهِ (Allahi) means "Allah (God)".', 2),
  ('quiz-sf-u1-l1-3', 'sf-u1-l1', 'What is the meaning of "الرَّحْمَنِ"?', 'the Most Gracious', ARRAY['Indeed You are', 'for me', 'and provide for me'], 'الرَّحْمَنِ (ar-Rahmani) means "the Most Gracious".', 3),
  ('quiz-sf-u1-l1-4', 'sf-u1-l1', 'What is the meaning of "الرَّحِيمِ"?', 'the Most Merciful', ARRAY['He begets', 'life', 'By time'], 'الرَّحِيمِ (ar-Raheemi) means "the Most Merciful".', 4),
  ('quiz-sf-u1-l1-5', 'sf-u1-l1', 'What is the meaning of "الْحَمْدُ"?', 'All praise', ARRAY['the Eternal Refuge', 'I seek refuge', 'righteous deeds'], 'الْحَمْدُ (al-Hamdu) means "All praise".', 5),
  ('quiz-sf-u1-l1-6', 'sf-u1-l1', 'What is the meaning of "لِلَّهِ"?', 'is for Allah', ARRAY['worship', 'My Lord', 'and blessed is'], 'لِلَّهِ (lillahi) means "is for Allah".', 6),
  ('quiz-sf-u1-l1-7', 'sf-u1-l1', 'What is the meaning of "رَبِّ"?', 'Lord of', ARRAY['family of', 'god/deity', 'the blowers'], 'رَبِّ (Rabbi) means "Lord of".', 7),
  ('quiz-sf-u1-l1-8', 'sf-u1-l1', 'What is the meaning of "الْعَالَمِينَ"?', 'the worlds', ARRAY['the Judgment', 'is He begotten', 'we worship'], 'الْعَالَمِينَ (al-''Aalameena) means "the worlds".', 8),
  ('quiz-sf-u1-l1-9', 'sf-u1-l1', 'Which word means "In the name of"?', 'بِسْمِ', ARRAY['لِلَّهِ', 'الرَّحِيمِ', 'الْحَمْدُ'], 'بِسْمِ (bismi) means "In the name of".', 9),
  ('quiz-sf-u1-l1-10', 'sf-u1-l1', 'Which word means "the Most Merciful"?', 'الرَّحِيمِ', ARRAY['رَبِّ', 'بِسْمِ', 'الرَّحْمَنِ'], 'الرَّحِيمِ (ar-Raheemi) means "the Most Merciful".', 10)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u1-l2-1', 'sf-u1-l2', 'What is the meaning of "مَالِكِ"?', 'Master/Owner of', ARRAY['the Hereafter', 'God of', 'O you'], 'مَالِكِ (Maliki) means "Master/Owner of".', 1),
  ('quiz-sf-u1-l2-2', 'sf-u1-l2', 'What is the meaning of "يَوْمِ"?', 'the Day of', ARRAY['the one who hates you', 'upon us', 'you worship'], 'يَوْمِ (Yawmi) means "the Day of".', 2),
  ('quiz-sf-u1-l2-3', 'sf-u1-l2', 'What is the meaning of "الدِّينِ"?', 'the Judgment', ARRAY['the jinn', 'His servant', 'the retreater'], 'الدِّينِ (ad-Deeni) means "the Judgment".', 3),
  ('quiz-sf-u1-l2-4', 'sf-u1-l2', 'What is the meaning of "إِيَّاكَ"?', 'You Alone', ARRAY['I bear witness', 'evil/harm of', 'the Fire'], 'إِيَّاكَ (Iyyaka) means "You Alone".', 4),
  ('quiz-sf-u1-l2-5', 'sf-u1-l2', 'What is the meaning of "نَعْبُدُ"?', 'we worship', ARRAY['Allah', 'the jinn', 'In the name of'], 'نَعْبُدُ (na''budu) means "we worship".', 5),
  ('quiz-sf-u1-l2-6', 'sf-u1-l2', 'What is the meaning of "نَسْتَعِينُ"?', 'we ask for help', ARRAY['O Prophet', 'praises Him', 'and to You (belongs)'], 'نَسْتَعِينُ (nasta''eenu) means "we ask for help".', 6),
  ('quiz-sf-u1-l2-7', 'sf-u1-l2', 'Which word means "the Day of"?', 'يَوْمِ', ARRAY['مَالِكِ', 'نَعْبُدُ', 'نَسْتَعِينُ'], 'يَوْمِ (Yawmi) means "the Day of".', 7),
  ('quiz-sf-u1-l2-8', 'sf-u1-l2', 'Which word means "we ask for help"?', 'نَسْتَعِينُ', ARRAY['نَعْبُدُ', 'إِيَّاكَ', 'يَوْمِ'], 'نَسْتَعِينُ (nasta''eenu) means "we ask for help".', 8)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u1-l3-1', 'sf-u1-l3', 'What is the meaning of "اهْدِنَا"?', 'Guide us', ARRAY['you worship', 'punishment of', 'and blessed is'], 'اهْدِنَا (Ihdina) means "Guide us".', 1),
  ('quiz-sf-u1-l3-2', 'sf-u1-l3', 'What is the meaning of "الصِّرَاطَ"?', 'the path', ARRAY['praises Him', 'not (past)', 'the Judgment'], 'الصِّرَاطَ (as-Sirata) means "the path".', 2),
  ('quiz-sf-u1-l3-3', 'sf-u1-l3', 'What is the meaning of "الْمُسْتَقِيمَ"?', 'the straight', ARRAY['and prayers', 'the retreater', 'the Most High'], 'الْمُسْتَقِيمَ (al-Mustaqeema) means "the straight".', 3),
  ('quiz-sf-u1-l3-4', 'sf-u1-l3', 'What is the meaning of "الَّذِينَ"?', 'those who', ARRAY['and exalted is', 'and His blessings', 'By time'], 'الَّذِينَ (alladheena) means "those who".', 4),
  ('quiz-sf-u1-l3-5', 'sf-u1-l3', 'What is the meaning of "أَنْعَمْتَ"?', 'You have blessed', ARRAY['with truth', 'what', 'the jinn'], 'أَنْعَمْتَ (an''amta) means "You have blessed".', 5),
  ('quiz-sf-u1-l3-6', 'sf-u1-l3', 'What is the meaning of "عَلَيْهِمْ"?', 'upon them', ARRAY['other than You', 'He is', 'you worship'], 'عَلَيْهِمْ (''alayhim) means "upon them".', 6),
  ('quiz-sf-u1-l3-7', 'sf-u1-l3', 'What is the meaning of "غَيْرِ"?', 'not (other than)', ARRAY['just as', 'worship', 'the Hereafter'], 'غَيْرِ (ghayri) means "not (other than)".', 7),
  ('quiz-sf-u1-l3-8', 'sf-u1-l3', 'What is the meaning of "الْمَغْضُوبِ"?', 'those who earned wrath', ARRAY['Muhammad', 'You Alone', 'evil/harm of'], 'الْمَغْضُوبِ (al-Maghdoobi) means "those who earned wrath".', 8),
  ('quiz-sf-u1-l3-9', 'sf-u1-l3', 'What is the meaning of "وَلَا"?', 'and not', ARRAY['my religion', 'He begets', 'and guide me'], 'وَلَا (wa la) means "and not".', 9),
  ('quiz-sf-u1-l3-10', 'sf-u1-l3', 'What is the meaning of "الضَّالِّينَ"?', 'those who go astray', ARRAY['did (deeds)', 'mankind', 'and sacrifice'], 'الضَّالِّينَ (ad-Daalleen) means "those who go astray".', 10),
  ('quiz-sf-u1-l3-11', 'sf-u1-l3', 'Which word means "not (other than)"?', 'غَيْرِ', ARRAY['عَلَيْهِمْ', 'الْمَغْضُوبِ', 'اهْدِنَا'], 'غَيْرِ (ghayri) means "not (other than)".', 11),
  ('quiz-sf-u1-l3-12', 'sf-u1-l3', 'Which word means "the straight"?', 'الْمُسْتَقِيمَ', ARRAY['عَلَيْهِمْ', 'الضَّالِّينَ', 'أَنْعَمْتَ'], 'الْمُسْتَقِيمَ (al-Mustaqeema) means "the straight".', 12)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-root-rhm-1', 'sf-root-rhm', 'Which of these words comes from the root ر-ح-م?', 'الرَّحْمَنِ', ARRAY['وَتَعَالَى', 'الصَّمَدُ', 'دِينُكُمْ'], 'الرَّحْمَنِ (ar-Rahmani) comes from the root ر-ح-م which means "mercy, compassion".', 1),
  ('quiz-sf-root-rhm-2', 'sf-root-rhm', 'What is the core meaning of the root ر-ح-م?', 'mercy, compassion', ARRAY['deceive, cover', 'split, daybreak', 'help, assistance'], 'The root ر-ح-م carries the meaning of "mercy, compassion".', 2),
  ('quiz-sf-root-rhm-3', 'sf-root-rhm', 'How many words you''ve learned come from the root ر-ح-م?', '4', ARRAY['1', '2', '3'], '4 words derive from ر-ح-م: الرَّحْمَنِ, الرَّحِيمِ, وَارْحَمْنِي, وَرَحْمَةُ.', 3)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-root-abd-1', 'sf-root-abd', 'Which of these words comes from the root ع-ب-د?', 'نَعْبُدُ', ARRAY['فَصَلِّ', 'غَيْرِ', 'وَبَرَكَاتُهُ'], 'نَعْبُدُ (na''budu) comes from the root ع-ب-د which means "worship, servitude".', 1),
  ('quiz-sf-root-abd-2', 'sf-root-abd', 'What is the core meaning of the root ع-ب-د?', 'worship, servitude', ARRAY['disbelieve, deny, cover', 'blessing, abundance', 'people, mankind'], 'The root ع-ب-د carries the meaning of "worship, servitude".', 2),
  ('quiz-sf-root-abd-3', 'sf-root-abd', 'How many words you''ve learned come from the root ع-ب-د?', '8', ARRAY['1', '2', '3'], '8 words derive from ع-ب-د: نَعْبُدُ, أَعْبُدُ, تَعْبُدُونَ, عَابِدُونَ, عَبَدتُّ, عَابِدٌ, عِبَادِ, عَبْدُهُ.', 3)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u2-l1-1', 'sf-u2-l1', 'What is the meaning of "قُلْ"?', 'Say', ARRAY['mankind', 'the blowers', 'those who go astray'], 'قُلْ (Qul) means "Say".', 1),
  ('quiz-sf-u2-l1-2', 'sf-u2-l1', 'What is the meaning of "هُوَ"?', 'He is', ARRAY['and death', 'O Prophet', 'Allah'], 'هُوَ (Huwa) means "He is".', 2),
  ('quiz-sf-u2-l1-3', 'sf-u2-l1', 'What is the meaning of "اللَّهُ"?', 'Allah', ARRAY['the disbelievers', 'god/deity', 'life'], 'اللَّهُ (Allahu) means "Allah".', 3),
  ('quiz-sf-u2-l1-4', 'sf-u2-l1', 'What is the meaning of "أَحَدٌ"?', 'One', ARRAY['Guide us', 'My Lord', 'and have mercy on me'], 'أَحَدٌ (Ahad) means "One".', 4),
  ('quiz-sf-u2-l1-5', 'sf-u2-l1', 'What is the meaning of "الصَّمَدُ"?', 'the Eternal Refuge', ARRAY['upon', 'worship', 'has heard'], 'الصَّمَدُ (as-Samad) means "the Eternal Refuge".', 5),
  ('quiz-sf-u2-l1-6', 'sf-u2-l1', 'What is the meaning of "لَمْ"?', 'not (past)', ARRAY['in the Lord of', 'forgive', 'my Lord'], 'لَمْ (lam) means "not (past)".', 6),
  ('quiz-sf-u2-l1-7', 'sf-u2-l1', 'What is the meaning of "يَلِدْ"?', 'He begets', ARRAY['your religion', 'O Prophet', 'the Most Merciful'], 'يَلِدْ (yalid) means "He begets".', 7),
  ('quiz-sf-u2-l1-8', 'sf-u2-l1', 'What is the meaning of "يُولَدْ"?', 'is He begotten', ARRAY['and with Your praise', 'O you', 'believed'], 'يُولَدْ (yoolad) means "is He begotten".', 8),
  ('quiz-sf-u2-l1-9', 'sf-u2-l1', 'What is the meaning of "يَكُن"?', 'there is', ARRAY['O you', 'your religion', 'an envier'], 'يَكُن (yakun) means "there is".', 9),
  ('quiz-sf-u2-l1-10', 'sf-u2-l1', 'What is the meaning of "كُفُوًا"?', 'equivalent/comparable', ARRAY['O Allah', 'the Judgment', 'when he envies'], 'كُفُوًا (kufuwan) means "equivalent/comparable".', 10),
  ('quiz-sf-u2-l1-11', 'sf-u2-l1', 'Which word means "is He begotten"?', 'يُولَدْ', ARRAY['هُوَ', 'الصَّمَدُ', 'قُلْ'], 'يُولَدْ (yoolad) means "is He begotten".', 11),
  ('quiz-sf-u2-l1-12', 'sf-u2-l1', 'Which word means "Allah"?', 'اللَّهُ', ARRAY['يَكُن', 'الصَّمَدُ', 'لَمْ'], 'اللَّهُ (Allahu) means "Allah".', 12)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u2-l2-1', 'sf-u2-l2', 'What is the meaning of "أَعُوذُ"?', 'I seek refuge', ARRAY['except', 'Your name', 'is for Allah'], 'أَعُوذُ (a''oodhu) means "I seek refuge".', 1),
  ('quiz-sf-u2-l2-2', 'sf-u2-l2', 'What is the meaning of "بِرَبِّ"?', 'in the Lord of', ARRAY['those who earned wrath', 'I bear witness', 'and the mercy of'], 'بِرَبِّ (bi-Rabbi) means "in the Lord of".', 2),
  ('quiz-sf-u2-l2-3', 'sf-u2-l2', 'What is the meaning of "الْفَلَقِ"?', 'the daybreak', ARRAY['all praise', 'and blessed is', 'Your name'], 'الْفَلَقِ (al-Falaq) means "the daybreak".', 3),
  ('quiz-sf-u2-l2-4', 'sf-u2-l2', 'What is the meaning of "شَرِّ"?', 'evil/harm of', ARRAY['and not', 'Lord of', 'breasts/hearts'], 'شَرِّ (sharri) means "evil/harm of".', 4),
  ('quiz-sf-u2-l2-5', 'sf-u2-l2', 'What is the meaning of "مَا"?', 'what', ARRAY['I seek refuge', 'is He begotten', 'and His blessings'], 'مَا (ma) means "what".', 5),
  ('quiz-sf-u2-l2-6', 'sf-u2-l2', 'What is the meaning of "خَلَقَ"?', 'He created', ARRAY['He is', 'His servant', 'and grant me wellbeing'], 'خَلَقَ (khalaqa) means "He created".', 6),
  ('quiz-sf-u2-l2-7', 'sf-u2-l2', 'What is the meaning of "غَاسِقٍ"?', 'darkness', ARRAY['Indeed You are', 'Lord of', 'send blessings'], 'غَاسِقٍ (ghasiqin) means "darkness".', 7),
  ('quiz-sf-u2-l2-8', 'sf-u2-l2', 'What is the meaning of "وَقَبَ"?', 'when it settles', ARRAY['worshippers of', 'He begets', 'and pure words'], 'وَقَبَ (waqab) means "when it settles".', 8),
  ('quiz-sf-u2-l2-9', 'sf-u2-l2', 'What is the meaning of "النَّفَّاثَاتِ"?', 'the blowers', ARRAY['and grant me wellbeing', 'worshippers of', 'who whispers'], 'النَّفَّاثَاتِ (an-Naffathati) means "the blowers".', 9),
  ('quiz-sf-u2-l2-10', 'sf-u2-l2', 'What is the meaning of "الْعُقَدِ"?', 'the knots', ARRAY['Muhammad', 'My Lord', 'Glory be to'], 'الْعُقَدِ (al-''Uqad) means "the knots".', 10),
  ('quiz-sf-u2-l2-11', 'sf-u2-l2', 'What is the meaning of "حَاسِدٍ"?', 'an envier', ARRAY['in You', 'except', 'Your name'], 'حَاسِدٍ (hasidin) means "an envier".', 11),
  ('quiz-sf-u2-l2-12', 'sf-u2-l2', 'What is the meaning of "حَسَدَ"?', 'when he envies', ARRAY['Lord of', 'for me', 'All greetings'], 'حَسَدَ (hasad) means "when he envies".', 12),
  ('quiz-sf-u2-l2-13', 'sf-u2-l2', 'Which word means "the daybreak"?', 'الْفَلَقِ', ARRAY['شَرِّ', 'مَا', 'خَلَقَ'], 'الْفَلَقِ (al-Falaq) means "the daybreak".', 13),
  ('quiz-sf-u2-l2-14', 'sf-u2-l2', 'Which word means "I seek refuge"?', 'أَعُوذُ', ARRAY['النَّفَّاثَاتِ', 'حَسَدَ', 'بِرَبِّ'], 'أَعُوذُ (a''oodhu) means "I seek refuge".', 14)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u2-l3-1', 'sf-u2-l3', 'What is the meaning of "النَّاسِ"?', 'mankind', ARRAY['upon you', 'there is', 'One'], 'النَّاسِ (an-Nas) means "mankind".', 1),
  ('quiz-sf-u2-l3-2', 'sf-u2-l3', 'What is the meaning of "مَلِكِ"?', 'King of', ARRAY['Ibrahim (Abraham)', 'did (deeds)', 'upon us'], 'مَلِكِ (Maliki) means "King of".', 2),
  ('quiz-sf-u2-l3-3', 'sf-u2-l3', 'What is the meaning of "إِلَهِ"?', 'God of', ARRAY['and exalted is', 'You Alone', 'we worship'], 'إِلَهِ (Ilahi) means "God of".', 3),
  ('quiz-sf-u2-l3-4', 'sf-u2-l3', 'What is the meaning of "الْوَسْوَاسِ"?', 'the whisperer', ARRAY['and grant me wellbeing', 'the worlds', 'my religion'], 'الْوَسْوَاسِ (al-Waswas) means "the whisperer".', 4),
  ('quiz-sf-u2-l3-5', 'sf-u2-l3', 'What is the meaning of "الْخَنَّاسِ"?', 'the retreater', ARRAY['and pure words', 'worship', 'give us'], 'الْخَنَّاسِ (al-Khannas) means "the retreater".', 5),
  ('quiz-sf-u2-l3-6', 'sf-u2-l3', 'What is the meaning of "يُوَسْوِسُ"?', 'who whispers', ARRAY['the straight', 'forgive', 'the Most Merciful'], 'يُوَسْوِسُ (yuwaswisu) means "who whispers".', 6),
  ('quiz-sf-u2-l3-7', 'sf-u2-l3', 'What is the meaning of "صُدُورِ"?', 'breasts/hearts', ARRAY['the blowers', 'the one cut off', 'worship'], 'صُدُورِ (sudoor) means "breasts/hearts".', 7),
  ('quiz-sf-u2-l3-8', 'sf-u2-l3', 'What is the meaning of "الْجِنَّةِ"?', 'the jinn', ARRAY['and to You (belongs)', 'believed', 'we worship'], 'الْجِنَّةِ (al-Jinnati) means "the jinn".', 8),
  ('quiz-sf-u2-l3-9', 'sf-u2-l3', 'Which word means "King of"?', 'مَلِكِ', ARRAY['النَّاسِ', 'إِلَهِ', 'الْخَنَّاسِ'], 'مَلِكِ (Maliki) means "King of".', 9),
  ('quiz-sf-u2-l3-10', 'sf-u2-l3', 'Which word means "the jinn"?', 'الْجِنَّةِ', ARRAY['الْخَنَّاسِ', 'يُوَسْوِسُ', 'إِلَهِ'], 'الْجِنَّةِ (al-Jinnati) means "the jinn".', 10)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-root-qwl-1', 'sf-root-qwl', 'Which of these words comes from the root ق-و-ل?', 'قُلْ', ARRAY['وَتَعَالَى', 'وَعَافِنِي', 'مَا'], 'قُلْ (Qul) comes from the root ق-و-ل which means "say, speech".', 1),
