INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u3-l1-1', 'sf-u3-l1', 'What is the meaning of "وَالْعَصْرِ"?', 'By time', ARRAY['in the Lord of', 'Allah', 'good/blessing'], 'وَالْعَصْرِ (wal-''Asr) means "By time".', 1),
  ('quiz-sf-u3-l1-2', 'sf-u3-l1', 'What is the meaning of "الْإِنسَانَ"?', 'mankind', ARRAY['Guide us', 'Indeed You are', 'the daybreak'], 'الْإِنسَانَ (al-Insana) means "mankind".', 2),
  ('quiz-sf-u3-l1-3', 'sf-u3-l1', 'What is the meaning of "خُسْرٍ"?', 'loss', ARRAY['for me', 'a worshipper', 'those who earned wrath'], 'خُسْرٍ (khusr) means "loss".', 3),
  ('quiz-sf-u3-l1-4', 'sf-u3-l1', 'What is the meaning of "آمَنُوا"?', 'believed', ARRAY['trial/tribulation of', 'so pray', 'in the Lord of'], 'آمَنُوا (amanoo) means "believed".', 4),
  ('quiz-sf-u3-l1-5', 'sf-u3-l1', 'What is the meaning of "عَمِلُوا"?', 'did (deeds)', ARRAY['He begets', 'the Hereafter', 'and His blessings'], 'عَمِلُوا (''amiloo) means "did (deeds)".', 5),
  ('quiz-sf-u3-l1-6', 'sf-u3-l1', 'What is the meaning of "الصَّالِحَاتِ"?', 'righteous deeds', ARRAY['God of', 'life', 'Your name'], 'الصَّالِحَاتِ (as-Salihati) means "righteous deeds".', 6),
  ('quiz-sf-u3-l1-7', 'sf-u3-l1', 'What is the meaning of "تَوَاصَوْا"?', 'advised each other', ARRAY['Your majesty', 'His servant', 'except'], 'تَوَاصَوْا (tawasaw) means "advised each other".', 7),
  ('quiz-sf-u3-l1-8', 'sf-u3-l1', 'What is the meaning of "بِالْحَقِّ"?', 'with truth', ARRAY['the Most Merciful', 'Glory be to', 'has heard'], 'بِالْحَقِّ (bil-Haqqi) means "with truth".', 8),
  ('quiz-sf-u3-l1-9', 'sf-u3-l1', 'What is the meaning of "بِالصَّبْرِ"?', 'with patience', ARRAY['forgive', 'life', 'I bear witness'], 'بِالصَّبْرِ (bis-Sabr) means "with patience".', 9),
  ('quiz-sf-u3-l1-10', 'sf-u3-l1', 'Which word means "loss"?', 'خُسْرٍ', ARRAY['تَوَاصَوْا', 'بِالصَّبْرِ', 'بِالْحَقِّ'], 'خُسْرٍ (khusr) means "loss".', 10),
  ('quiz-sf-u3-l1-11', 'sf-u3-l1', 'Which word means "with patience"?', 'بِالصَّبْرِ', ARRAY['الْإِنسَانَ', 'عَمِلُوا', 'الصَّالِحَاتِ'], 'بِالصَّبْرِ (bis-Sabr) means "with patience".', 11)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u3-l2-1', 'sf-u3-l2', 'What is the meaning of "أَعْطَيْنَاكَ"?', 'We have given you', ARRAY['the Day of', 'is He begotten', 'Peace'], 'أَعْطَيْنَاكَ (a''taynaaka) means "We have given you".', 1),
  ('quiz-sf-u3-l2-2', 'sf-u3-l2', 'What is the meaning of "الْكَوْثَرَ"?', 'the Abundance', ARRAY['a worshipper', 'Glory be to', 'and death'], 'الْكَوْثَرَ (al-Kawthar) means "the Abundance".', 2),
  ('quiz-sf-u3-l2-3', 'sf-u3-l2', 'What is the meaning of "فَصَلِّ"?', 'so pray', ARRAY['Allah', 'Praiseworthy', 'Indeed You are'], 'فَصَلِّ (fa-salli) means "so pray".', 3),
  ('quiz-sf-u3-l2-4', 'sf-u3-l2', 'What is the meaning of "لِرَبِّكَ"?', 'to your Lord', ARRAY['your religion', 'Say', 'You Alone'], 'لِرَبِّكَ (li-Rabbika) means "to your Lord".', 4),
  ('quiz-sf-u3-l2-5', 'sf-u3-l2', 'What is the meaning of "وَانْحَرْ"?', 'and sacrifice', ARRAY['Your majesty', 'we ask for help', 'and have mercy on me'], 'وَانْحَرْ (wanhar) means "and sacrifice".', 5),
  ('quiz-sf-u3-l2-6', 'sf-u3-l2', 'What is the meaning of "شَانِئَكَ"?', 'the one who hates you', ARRAY['what', 'in the Lord of', 'the Day of'], 'شَانِئَكَ (shani''aka) means "the one who hates you".', 6),
  ('quiz-sf-u3-l2-7', 'sf-u3-l2', 'What is the meaning of "الْأَبْتَرُ"?', 'the one cut off', ARRAY['Our Lord', 'Guide us', 'Your majesty'], 'الْأَبْتَرُ (al-Abtar) means "the one cut off".', 7),
  ('quiz-sf-u3-l2-8', 'sf-u3-l2', 'Which word means "so pray"?', 'فَصَلِّ', ARRAY['لِرَبِّكَ', 'الْكَوْثَرَ', 'الْأَبْتَرُ'], 'فَصَلِّ (fa-salli) means "so pray".', 8),
  ('quiz-sf-u3-l2-9', 'sf-u3-l2', 'Which word means "the one cut off"?', 'الْأَبْتَرُ', ARRAY['فَصَلِّ', 'لِرَبِّكَ', 'أَعْطَيْنَاكَ'], 'الْأَبْتَرُ (al-Abtar) means "the one cut off".', 9)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u3-l3-1', 'sf-u3-l3', 'What is the meaning of "أَيُّهَا"?', 'O you', ARRAY['equivalent/comparable', 'the Magnificent', 'with patience'], 'أَيُّهَا (ayyuha) means "O you".', 1),
  ('quiz-sf-u3-l3-2', 'sf-u3-l3', 'What is the meaning of "الْكَافِرُونَ"?', 'the disbelievers', ARRAY['and protect us from', 'is He begotten', 'Peace'], 'الْكَافِرُونَ (al-Kafiroon) means "the disbelievers".', 2),
  ('quiz-sf-u3-l3-3', 'sf-u3-l3', 'What is the meaning of "لَا"?', 'I do not', ARRAY['He begets', 'the jinn', 'My Lord'], 'لَا (la) means "I do not".', 3),
  ('quiz-sf-u3-l3-4', 'sf-u3-l3', 'What is the meaning of "أَعْبُدُ"?', 'worship', ARRAY['One', 'and death', 'mankind'], 'أَعْبُدُ (a''budu) means "worship".', 4),
  ('quiz-sf-u3-l3-5', 'sf-u3-l3', 'What is the meaning of "تَعْبُدُونَ"?', 'you worship', ARRAY['and death', 'the Hereafter', 'with patience'], 'تَعْبُدُونَ (ta''budoona) means "you worship".', 5),
  ('quiz-sf-u3-l3-6', 'sf-u3-l3', 'What is the meaning of "عَابِدُونَ"?', 'worshippers of', ARRAY['the Most High', 'and His messenger', 'other than You'], 'عَابِدُونَ (''aabidoona) means "worshippers of".', 6),
  ('quiz-sf-u3-l3-7', 'sf-u3-l3', 'What is the meaning of "عَبَدتُّ"?', 'I have worshipped', ARRAY['Ibrahim (Abraham)', 'Say', 'and protect us from'], 'عَبَدتُّ (''abadtu) means "I have worshipped".', 7),
  ('quiz-sf-u3-l3-8', 'sf-u3-l3', 'What is the meaning of "عَابِدٌ"?', 'a worshipper', ARRAY['and sacrifice', 'is He begotten', 'All greetings'], 'عَابِدٌ (''aabid) means "a worshipper".', 8),
  ('quiz-sf-u3-l3-9', 'sf-u3-l3', 'What is the meaning of "دِينُكُمْ"?', 'your religion', ARRAY['is He begotten', 'the one who', 'in the Lord of'], 'دِينُكُمْ (deenukum) means "your religion".', 9),
  ('quiz-sf-u3-l3-10', 'sf-u3-l3', 'What is the meaning of "دِينِ"?', 'my religion', ARRAY['the blowers', 'and blessed is', 'family of'], 'دِينِ (deeni) means "my religion".', 10),
