  ('quiz-sf-u3-l3-12', 'sf-u3-l3', 'Which word means "O you"?', 'أَيُّهَا', ARRAY['عَبَدتُّ', 'عَابِدٌ', 'عَابِدُونَ'], 'أَيُّهَا (ayyuha) means "O you".', 12)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-root-aml-1', 'sf-root-aml', 'Which of these words comes from the root ع-م-ل?', 'عَمِلُوا', ARRAY['عَلَى', 'أَحَدٌ', 'الْعَظِيمِ'], 'عَمِلُوا (''amiloo) comes from the root ع-م-ل which means "action, deed, work".', 1),
  ('quiz-sf-root-aml-2', 'sf-root-aml', 'What is the core meaning of the root ع-م-ل?', 'action, deed, work', ARRAY['chest, breast', 'trial, temptation, test', 'anger, wrath'], 'The root ع-م-ل carries the meaning of "action, deed, work".', 2)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u4-l1-1', 'sf-u4-l1', 'What is the meaning of "سُبْحَانَكَ"?', 'Glory be to You', ARRAY['in the Lord of', 'You Alone', 'O Prophet'], 'سُبْحَانَكَ (Subhanaka) means "Glory be to You".', 1),
  ('quiz-sf-u4-l1-2', 'sf-u4-l1', 'What is the meaning of "اللَّهُمَّ"?', 'O Allah', ARRAY['is He begotten', 'we ask for help', 'loss'], 'اللَّهُمَّ (Allahumma) means "O Allah".', 2),
  ('quiz-sf-u4-l1-3', 'sf-u4-l1', 'What is the meaning of "وَبِحَمْدِكَ"?', 'and with Your praise', ARRAY['those who earned wrath', 'You Alone', 'Allah'], 'وَبِحَمْدِكَ (wa bi-Hamdika) means "and with Your praise".', 3),
  ('quiz-sf-u4-l1-4', 'sf-u4-l1', 'What is the meaning of "وَتَبَارَكَ"?', 'and blessed is', ARRAY['Glory be to', 'and provide for me', 'my Lord'], 'وَتَبَارَكَ (wa Tabaraka) means "and blessed is".', 4),
  ('quiz-sf-u4-l1-5', 'sf-u4-l1', 'What is the meaning of "اسْمُكَ"?', 'Your name', ARRAY['the Fire', 'King of', 'the jinn'], 'اسْمُكَ (Ismuka) means "Your name".', 5),
  ('quiz-sf-u4-l1-6', 'sf-u4-l1', 'What is the meaning of "وَتَعَالَى"?', 'and exalted is', ARRAY['you worship', 'He begets', 'Glory be to'], 'وَتَعَالَى (wa Ta''aala) means "and exalted is".', 6),
  ('quiz-sf-u4-l1-7', 'sf-u4-l1', 'What is the meaning of "جَدُّكَ"?', 'Your majesty', ARRAY['what', 'those who', 'the Abundance'], 'جَدُّكَ (Jadduka) means "Your majesty".', 7),
  ('quiz-sf-u4-l1-8', 'sf-u4-l1', 'What is the meaning of "إِلَهَ"?', 'god/deity', ARRAY['He is', 'the one cut off', 'the blowers'], 'إِلَهَ (ilaha) means "god/deity".', 8),
  ('quiz-sf-u4-l1-9', 'sf-u4-l1', 'What is the meaning of "غَيْرُكَ"?', 'other than You', ARRAY['when it settles', 'good/blessing', 'worship'], 'غَيْرُكَ (ghayruka) means "other than You".', 9),
  ('quiz-sf-u4-l1-10', 'sf-u4-l1', 'Which word means "and exalted is"?', 'وَتَعَالَى', ARRAY['وَبِحَمْدِكَ', 'غَيْرُكَ', 'اللَّهُمَّ'], 'وَتَعَالَى (wa Ta''aala) means "and exalted is".', 10),
  ('quiz-sf-u4-l1-11', 'sf-u4-l1', 'Which word means "and with Your praise"?', 'وَبِحَمْدِكَ', ARRAY['وَتَبَارَكَ', 'سُبْحَانَكَ', 'اللَّهُمَّ'], 'وَبِحَمْدِكَ (wa bi-Hamdika) means "and with Your praise".', 11)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u4-l2-1', 'sf-u4-l2', 'What is the meaning of "سُبْحَانَ"?', 'Glory be to', ARRAY['worship', 'and prayers', 'breasts/hearts'], 'سُبْحَانَ (Subhana) means "Glory be to".', 1),
  ('quiz-sf-u4-l2-2', 'sf-u4-l2', 'What is the meaning of "رَبِّيَ"?', 'my Lord', ARRAY['evil/harm of', 'the Day of', 'the one who hates you'], 'رَبِّيَ (Rabbiya) means "my Lord".', 2),
  ('quiz-sf-u4-l2-3', 'sf-u4-l2', 'What is the meaning of "الْعَظِيمِ"?', 'the Magnificent', ARRAY['and prayers', 'Allah (God)', 'You sent blessings'], 'الْعَظِيمِ (al-''Adheemi) means "the Magnificent".', 3),
  ('quiz-sf-u4-l2-4', 'sf-u4-l2', 'What is the meaning of "سَمِعَ"?', 'has heard', ARRAY['Your name', 'is He begotten', 'I have worshipped'], 'سَمِعَ (Sami''a) means "has heard".', 4),
  ('quiz-sf-u4-l2-5', 'sf-u4-l2', 'What is the meaning of "اللَّهُ"?', 'Allah', ARRAY['Glorious', 'upon them', 'there is'], 'اللَّهُ (Allahu) means "Allah".', 5),
  ('quiz-sf-u4-l2-6', 'sf-u4-l2', 'What is the meaning of "لِمَنْ"?', 'the one who', ARRAY['the Judgment', 'the Most Merciful', 'just as'], 'لِمَنْ (liman) means "the one who".', 6),
  ('quiz-sf-u4-l2-7', 'sf-u4-l2', 'What is the meaning of "حَمِدَهُ"?', 'praises Him', ARRAY['worship', 'the knots', 'and His blessings'], 'حَمِدَهُ (hamidahu) means "praises Him".', 7),
  ('quiz-sf-u4-l2-8', 'sf-u4-l2', 'What is the meaning of "رَبَّنَا"?', 'Our Lord', ARRAY['except', 'is He begotten', 'darkness'], 'رَبَّنَا (Rabbana) means "Our Lord".', 8),
  ('quiz-sf-u4-l2-9', 'sf-u4-l2', 'What is the meaning of "وَلَكَ"?', 'and to You (belongs)', ARRAY['O you', 'other than You', 'and with Your praise'], 'وَلَكَ (wa laka) means "and to You (belongs)".', 9),
  ('quiz-sf-u4-l2-10', 'sf-u4-l2', 'What is the meaning of "الْحَمْدُ"?', 'all praise', ARRAY['my religion', 'there is', 'when he envies'], 'الْحَمْدُ (al-Hamd) means "all praise".', 10),
  ('quiz-sf-u4-l2-11', 'sf-u4-l2', 'Which word means "all praise"?', 'الْحَمْدُ', ARRAY['حَمِدَهُ', 'رَبِّيَ', 'لِمَنْ'], 'الْحَمْدُ (al-Hamd) means "all praise".', 11),
  ('quiz-sf-u4-l2-12', 'sf-u4-l2', 'Which word means "the Magnificent"?', 'الْعَظِيمِ', ARRAY['الْحَمْدُ', 'لِمَنْ', 'سَمِعَ'], 'الْعَظِيمِ (al-''Adheemi) means "the Magnificent".', 12)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-root-sbh-1', 'sf-root-sbh', 'Which of these words comes from the root س-ب-ح?', 'سُبْحَانَكَ', ARRAY['عَلَى', 'مَالِكِ', 'وَقِنَا'], 'سُبْحَانَكَ (Subhanaka) comes from the root س-ب-ح which means "glory, exalt, swim".', 1),
  ('quiz-sf-root-sbh-2', 'sf-root-sbh', 'What is the core meaning of the root س-ب-ح?', 'glory, exalt, swim', ARRAY['punishment, torment', 'hate, detest', 'evil, harm'], 'The root س-ب-ح carries the meaning of "glory, exalt, swim".', 2),
  ('quiz-sf-root-sbh-3', 'sf-root-sbh', 'How many words you''ve learned come from the root س-ب-ح?', '2', ARRAY['1', '3', '4'], '2 words derive from س-ب-ح: سُبْحَانَكَ, سُبْحَانَ.', 3)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-root-hmd-1', 'sf-root-hmd', 'Which of these words comes from the root ح-م-د?', 'الْحَمْدُ', ARRAY['وَالصَّلَوَاتُ', 'الضَّالِّينَ', 'الرَّحِيمِ'], 'الْحَمْدُ (al-Hamdu) comes from the root ح-م-د which means "praise, commendation".', 1),
  ('quiz-sf-root-hmd-2', 'sf-root-hmd', 'What is the core meaning of the root ح-م-د?', 'praise, commendation', ARRAY['king, dominion, authority', 'give, grant', 'people, mankind'], 'The root ح-م-د carries the meaning of "praise, commendation".', 2),
  ('quiz-sf-root-hmd-3', 'sf-root-hmd', 'How many words you''ve learned come from the root ح-م-د?', '6', ARRAY['1', '2', '3'], '6 words derive from ح-م-د: الْحَمْدُ, وَبِحَمْدِكَ, حَمِدَهُ, الْحَمْدُ, مُحَمَّدٍ, حَمِيدٌ.', 3)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u5-l1-1', 'sf-u5-l1', 'What is the meaning of "الْأَعْلَى"?', 'the Most High', ARRAY['O Prophet', 'All greetings', 'not (other than)'], 'الْأَعْلَى (al-A''la) means "the Most High".', 1),
  ('quiz-sf-u5-l1-2', 'sf-u5-l1', 'What is the meaning of "رَبِّ"?', 'My Lord', ARRAY['and protect us from', 'all praise', 'We have given you'], 'رَبِّ (Rabbi) means "My Lord".', 2),
  ('quiz-sf-u5-l1-3', 'sf-u5-l1', 'What is the meaning of "اغْفِرْ"?', 'forgive', ARRAY['Glory be to You', 'and death', 'the whisperer'], 'اغْفِرْ (ighfir) means "forgive".', 3),
  ('quiz-sf-u5-l1-4', 'sf-u5-l1', 'What is the meaning of "لِي"?', 'for me', ARRAY['Allah', 'the Fire', 'and His messenger'], 'لِي (li) means "for me".', 4),
  ('quiz-sf-u5-l1-5', 'sf-u5-l1', 'What is the meaning of "وَارْحَمْنِي"?', 'and have mercy on me', ARRAY['All praise', 'good/blessing', 'God of'], 'وَارْحَمْنِي (warhamni) means "and have mercy on me".', 5),
  ('quiz-sf-u5-l1-6', 'sf-u5-l1', 'What is the meaning of "وَاهْدِنِي"?', 'and guide me', ARRAY['Peace', 'Ibrahim (Abraham)', 'when it settles'], 'وَاهْدِنِي (wahdini) means "and guide me".', 6),
  ('quiz-sf-u5-l1-7', 'sf-u5-l1', 'What is the meaning of "وَارْزُقْنِي"?', 'and provide for me', ARRAY['your religion', 'I seek refuge', 'and pure words'], 'وَارْزُقْنِي (warzuqni) means "and provide for me".', 7),
  ('quiz-sf-u5-l1-8', 'sf-u5-l1', 'What is the meaning of "وَعَافِنِي"?', 'and grant me wellbeing', ARRAY['and pure words', 'Allah', 'Your majesty'], 'وَعَافِنِي (wa''afini) means "and grant me wellbeing".', 8),
  ('quiz-sf-u5-l1-9', 'sf-u5-l1', 'Which word means "and grant me wellbeing"?', 'وَعَافِنِي', ARRAY['وَاهْدِنِي', 'لِي', 'وَارْحَمْنِي'], 'وَعَافِنِي (wa''afini) means "and grant me wellbeing".', 9),
  ('quiz-sf-u5-l1-10', 'sf-u5-l1', 'Which word means "My Lord"?', 'رَبِّ', ARRAY['وَارْحَمْنِي', 'الْأَعْلَى', 'لِي'], 'رَبِّ (Rabbi) means "My Lord".', 10)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u5-l2-1', 'sf-u5-l2', 'What is the meaning of "التَّحِيَّاتُ"?', 'All greetings', ARRAY['you worship', 'and blessed is', 'All praise'], 'التَّحِيَّاتُ (at-Tahiyyatu) means "All greetings".', 1),
  ('quiz-sf-u5-l2-2', 'sf-u5-l2', 'What is the meaning of "وَالصَّلَوَاتُ"?', 'and prayers', ARRAY['the one cut off', 'the straight', 'Our Lord'], 'وَالصَّلَوَاتُ (was-Salawatu) means "and prayers".', 2),
  ('quiz-sf-u5-l2-3', 'sf-u5-l2', 'What is the meaning of "وَالطَّيِّبَاتُ"?', 'and pure words', ARRAY['and exalted is', 'King of', 'and death'], 'وَالطَّيِّبَاتُ (wat-Tayyibatu) means "and pure words".', 3),
  ('quiz-sf-u5-l2-4', 'sf-u5-l2', 'What is the meaning of "السَّلَامُ"?', 'Peace', ARRAY['the daybreak', 'the disbelievers', 'and provide for me'], 'السَّلَامُ (as-Salamu) means "Peace".', 4),
  ('quiz-sf-u5-l2-5', 'sf-u5-l2', 'What is the meaning of "عَلَيْكَ"?', 'upon you', ARRAY['you worship', 'King of', 'Your name'], 'عَلَيْكَ (''alayka) means "upon you".', 5),
  ('quiz-sf-u5-l2-6', 'sf-u5-l2', 'What is the meaning of "النَّبِيُّ"?', 'O Prophet', ARRAY['the Most High', 'the Day of', 'the blowers'], 'النَّبِيُّ (an-Nabiyyu) means "O Prophet".', 6),
  ('quiz-sf-u5-l2-7', 'sf-u5-l2', 'What is the meaning of "وَرَحْمَةُ"?', 'and the mercy of', ARRAY['evil/harm of', 'has heard', 'your religion'], 'وَرَحْمَةُ (wa Rahmatu) means "and the mercy of".', 7),
  ('quiz-sf-u5-l2-8', 'sf-u5-l2', 'What is the meaning of "وَبَرَكَاتُهُ"?', 'and His blessings', ARRAY['not (other than)', 'we ask for help', 'with patience'], 'وَبَرَكَاتُهُ (wa Barakatuhu) means "and His blessings".', 8),
  ('quiz-sf-u5-l2-9', 'sf-u5-l2', 'What is the meaning of "عَلَيْنَا"?', 'upon us', ARRAY['the Most High', 'breasts/hearts', 'He begets'], 'عَلَيْنَا (''alayna) means "upon us".', 9),
  ('quiz-sf-u5-l2-10', 'sf-u5-l2', 'What is the meaning of "عِبَادِ"?', 'servants of', ARRAY['the path', 'Guide us', 'You sent blessings'], 'عِبَادِ (''ibadi) means "servants of".', 10),
  ('quiz-sf-u5-l2-11', 'sf-u5-l2', 'What is the meaning of "الصَّالِحِينَ"?', 'the righteous', ARRAY['Glorious', 'good/blessing', 'those who'], 'الصَّالِحِينَ (as-Saliheen) means "the righteous".', 11),
  ('quiz-sf-u5-l2-12', 'sf-u5-l2', 'What is the meaning of "أَشْهَدُ"?', 'I bear witness', ARRAY['the Most Merciful', 'You sent blessings', 'Ibrahim (Abraham)'], 'أَشْهَدُ (Ash-hadu) means "I bear witness".', 12),
  ('quiz-sf-u5-l2-13', 'sf-u5-l2', 'What is the meaning of "إِلَّا"?', 'except', ARRAY['believed', 'you worship', 'not (other than)'], 'إِلَّا (illa) means "except".', 13),
  ('quiz-sf-u5-l2-14', 'sf-u5-l2', 'What is the meaning of "مُحَمَّدًا"?', 'Muhammad', ARRAY['Allah', 'so pray', 'and exalted is'], 'مُحَمَّدًا (Muhammadan) means "Muhammad".', 14),
  ('quiz-sf-u5-l2-15', 'sf-u5-l2', 'What is the meaning of "عَبْدُهُ"?', 'His servant', ARRAY['and death', 'in this world', 'those who'], 'عَبْدُهُ (''abduhu) means "His servant".', 15),
  ('quiz-sf-u5-l2-16', 'sf-u5-l2', 'What is the meaning of "وَرَسُولُهُ"?', 'and His messenger', ARRAY['and grant me wellbeing', 'the worlds', 'You Alone'], 'وَرَسُولُهُ (wa Rasooluhu) means "and His messenger".', 16),
  ('quiz-sf-u5-l2-17', 'sf-u5-l2', 'Which word means "and prayers"?', 'وَالصَّلَوَاتُ', ARRAY['التَّحِيَّاتُ', 'وَرَحْمَةُ', 'عِبَادِ'], 'وَالصَّلَوَاتُ (was-Salawatu) means "and prayers".', 17),
  ('quiz-sf-u5-l2-18', 'sf-u5-l2', 'Which word means "His servant"?', 'عَبْدُهُ', ARRAY['السَّلَامُ', 'وَالطَّيِّبَاتُ', 'التَّحِيَّاتُ'], 'عَبْدُهُ (''abduhu) means "His servant".', 18)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-root-ghfr-1', 'sf-root-ghfr', 'Which of these words comes from the root غ-ف-ر?', 'اغْفِرْ', ARRAY['يُوَسْوِسُ', 'وَتَعَالَى', 'إِلَهِ'], 'اغْفِرْ (ighfir) comes from the root غ-ف-ر which means "forgive, cover, pardon".', 1),
  ('quiz-sf-root-ghfr-2', 'sf-root-ghfr', 'What is the core meaning of the root غ-ف-ر?', 'forgive, cover, pardon', ARRAY['king, dominion, authority', 'tie, knot', 'to know, worlds'], 'The root غ-ف-ر carries the meaning of "forgive, cover, pardon".', 2)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u6-l1-1', 'sf-u6-l1', 'What is the meaning of "صَلِّ"?', 'send blessings', ARRAY['God of', 'and guide me', 'King of'], 'صَلِّ (salli) means "send blessings".', 1),
  ('quiz-sf-u6-l1-2', 'sf-u6-l1', 'What is the meaning of "عَلَى"?', 'upon', ARRAY['not (other than)', 'You Alone', 'and the mercy of'], 'عَلَى (''ala) means "upon".', 2),
  ('quiz-sf-u6-l1-3', 'sf-u6-l1', 'What is the meaning of "مُحَمَّدٍ"?', 'Muhammad', ARRAY['Allah', 'the worlds', 'the Abundance'], 'مُحَمَّدٍ (Muhammadin) means "Muhammad".', 3),
  ('quiz-sf-u6-l1-4', 'sf-u6-l1', 'What is the meaning of "آلِ"?', 'family of', ARRAY['and pure words', 'punishment of', 'Your majesty'], 'آلِ (Aali) means "family of".', 4),
  ('quiz-sf-u6-l1-5', 'sf-u6-l1', 'What is the meaning of "كَمَا"?', 'just as', ARRAY['I do not', 'My Lord', 'to your Lord'], 'كَمَا (kama) means "just as".', 5),
  ('quiz-sf-u6-l1-6', 'sf-u6-l1', 'What is the meaning of "صَلَّيْتَ"?', 'You sent blessings', ARRAY['the knots', 'Allah', 'Glory be to You'], 'صَلَّيْتَ (sallayta) means "You sent blessings".', 6),
  ('quiz-sf-u6-l1-7', 'sf-u6-l1', 'What is the meaning of "إِبْرَاهِيمَ"?', 'Ibrahim (Abraham)', ARRAY['and provide for me', 'life', 'and pure words'], 'إِبْرَاهِيمَ (Ibraheema) means "Ibrahim (Abraham)".', 7),
  ('quiz-sf-u6-l1-8', 'sf-u6-l1', 'What is the meaning of "إِنَّكَ"?', 'Indeed You are', ARRAY['what', 'the Judgment', 'the Abundance'], 'إِنَّكَ (Innaka) means "Indeed You are".', 8),
  ('quiz-sf-u6-l1-9', 'sf-u6-l1', 'What is the meaning of "حَمِيدٌ"?', 'Praiseworthy', ARRAY['my religion', 'in this world', 'worshippers of'], 'حَمِيدٌ (Hameedun) means "Praiseworthy".', 9),
  ('quiz-sf-u6-l1-10', 'sf-u6-l1', 'What is the meaning of "مَجِيدٌ"?', 'Glorious', ARRAY['in You', 'He created', 'You Alone'], 'مَجِيدٌ (Majeedun) means "Glorious".', 10),
  ('quiz-sf-u6-l1-11', 'sf-u6-l1', 'Which word means "family of"?', 'آلِ', ARRAY['حَمِيدٌ', 'إِنَّكَ', 'عَلَى'], 'آلِ (Aali) means "family of".', 11),
  ('quiz-sf-u6-l1-12', 'sf-u6-l1', 'Which word means "upon"?', 'عَلَى', ARRAY['صَلَّيْتَ', 'حَمِيدٌ', 'صَلِّ'], 'عَلَى (''ala) means "upon".', 12)
INSERT INTO public.lesson_quiz_questions (id, lesson_id, question, correct_answer, wrong_answers, explanation, "order") VALUES
  ('quiz-sf-u6-l2-1', 'sf-u6-l2', 'What is the meaning of "آتِنَا"?', 'give us', ARRAY['is for Allah', 'the Eternal Refuge', 'when it settles'], 'آتِنَا (aatina) means "give us".', 1),
  ('quiz-sf-u6-l2-2', 'sf-u6-l2', 'What is the meaning of "فِي الدُّنْيَا"?', 'in this world', ARRAY['Master/Owner of', 'You sent blessings', 'I bear witness'], 'فِي الدُّنْيَا (fid-dunya) means "in this world".', 2),
  ('quiz-sf-u6-l2-3', 'sf-u6-l2', 'What is the meaning of "حَسَنَةً"?', 'good/blessing', ARRAY['servants of', 'and blessed is', 'worship'], 'حَسَنَةً (hasanatan) means "good/blessing".', 3),
  ('quiz-sf-u6-l2-4', 'sf-u6-l2', 'What is the meaning of "الْآخِرَةِ"?', 'the Hereafter', ARRAY['those who', 'upon them', 'By time'], 'الْآخِرَةِ (al-Aakhirati) means "the Hereafter".', 4),
  ('quiz-sf-u6-l2-5', 'sf-u6-l2', 'What is the meaning of "وَقِنَا"?', 'and protect us from', ARRAY['breasts/hearts', 'advised each other', 'You Alone'], 'وَقِنَا (wa qina) means "and protect us from".', 5),
  ('quiz-sf-u6-l2-6', 'sf-u6-l2', 'What is the meaning of "عَذَابَ"?', 'punishment of', ARRAY['By time', 'my Lord', 'you worship'], 'عَذَابَ (''adhaba) means "punishment of".', 6),
  ('quiz-sf-u6-l2-7', 'sf-u6-l2', 'What is the meaning of "النَّارِ"?', 'the Fire', ARRAY['I seek refuge', 'O you', 'the Day of'], 'النَّارِ (an-Nari) means "the Fire".', 7),
  ('quiz-sf-u6-l2-8', 'sf-u6-l2', 'What is the meaning of "أَعُوذُ"?', 'I seek refuge', ARRAY['Your name', 'is He begotten', 'and guide me'], 'أَعُوذُ (a''oodhu) means "I seek refuge".', 8),
  ('quiz-sf-u6-l2-9', 'sf-u6-l2', 'What is the meaning of "بِكَ"?', 'in You', ARRAY['has heard', 'evil/harm of', 'when he envies'], 'بِكَ (bika) means "in You".', 9),
  ('quiz-sf-u6-l2-10', 'sf-u6-l2', 'What is the meaning of "فِتْنَةِ"?', 'trial/tribulation of', ARRAY['the whisperer', 'All praise', 'the straight'], 'فِتْنَةِ (fitnati) means "trial/tribulation of".', 10),
  ('quiz-sf-u6-l2-11', 'sf-u6-l2', 'What is the meaning of "الْمَحْيَا"?', 'life', ARRAY['the daybreak', 'all praise', 'evil/harm of'], 'الْمَحْيَا (al-Mahya) means "life".', 11),
  ('quiz-sf-u6-l2-12', 'sf-u6-l2', 'What is the meaning of "وَالْمَمَاتِ"?', 'and death', ARRAY['is He begotten', 'the Judgment', 'the jinn'], 'وَالْمَمَاتِ (wal-Mamati) means "and death".', 12),
  ('quiz-sf-u6-l2-13', 'sf-u6-l2', 'Which word means "life"?', 'الْمَحْيَا', ARRAY['حَسَنَةً', 'فِي الدُّنْيَا', 'فِتْنَةِ'], 'الْمَحْيَا (al-Mahya) means "life".', 13),
  ('quiz-sf-u6-l2-14', 'sf-u6-l2', 'Which word means "the Fire"?', 'النَّارِ', ARRAY['حَسَنَةً', 'الْآخِرَةِ', 'فِي الدُّنْيَا'], 'النَّارِ (an-Nari) means "the Fire".', 14)
